// Parse AI response and build SQL query
const aiResponse = $input.item.json;

// Get original query
const originalQuery = $input.first().json.query || 'investimenti generici';

// Extract criteria from AI response - FIX: handle LangChain response format
let criteria;
try {
  // Try different response formats
  let content;

  if (Array.isArray(aiResponse) && aiResponse[0]?.message?.content) {
    // LangChain format: array with choices
    content = aiResponse[0].message.content;
  } else if (aiResponse.choices?.[0]?.message?.content) {
    // OpenAI API format
    content = aiResponse.choices[0].message.content;
  } else if (aiResponse.message?.content) {
    // Direct message format
    content = aiResponse.message.content;
  } else if (aiResponse.output) {
    // Output format
    content = aiResponse.output;
  } else if (aiResponse.text) {
    // Text format
    content = aiResponse.text;
  } else {
    // Fallback
    content = JSON.stringify(aiResponse);
  }

  // Clean markdown code blocks
  const cleaned = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  criteria = JSON.parse(cleaned);

  console.log('Parsed criteria:', criteria);
} catch (e) {
  console.error('Failed to parse AI criteria:', e);
  criteria = { sectors: [], assetTypes: ['stock', 'etf'], riskLevel: null, keywords: [] };
}

// Build WHERE clause
let whereConditions = [];

// Asset types filter
if (criteria.assetTypes && criteria.assetTypes.length > 0) {
  const types = criteria.assetTypes.map(t => `'${t}'`).join(',');
  whereConditions.push(`a.type::text IN (${types})`);
} else {
  whereConditions.push("a.type::text IN ('stock', 'etf', 'crypto')");
}

// Price filter
whereConditions.push('a.current_price > 0');

// Sectors filter - IMPORTANTE!
if (criteria.sectors && criteria.sectors.length > 0) {
  const sectors = criteria.sectors.map(s => `'${s}'`).join(',');
  whereConditions.push(`a.sector IN (${sectors})`);
}

// Keywords filter
if (criteria.keywords && criteria.keywords.length > 0) {
  const keywordConditions = criteria.keywords.map(kw =>
    `(LOWER(a.name) LIKE '%${kw.toLowerCase()}%' OR LOWER(a.symbol) LIKE '%${kw.toLowerCase()}%')`
  ).join(' OR ');
  whereConditions.push(`(${keywordConditions})`);
}

const whereClause = whereConditions.join(' AND ');

// Build ORDER BY
let orderBy = `
  CASE
    WHEN a.sector IS NOT NULL THEN 0
    ELSE 1
  END,
  a.market_cap DESC NULLS LAST,
  a.volume DESC
`;

if (criteria.sectors && criteria.sectors.length > 0) {
  const sectorCases = criteria.sectors.map((s, i) =>
    `WHEN a.sector = '${s}' THEN ${i}`
  ).join(' ');
  orderBy = `
    CASE ${sectorCases} ELSE 100 END,
    a.market_cap DESC NULLS LAST,
    a.volume DESC
  `;
}

const query = `
SELECT
  a.id,
  a.symbol,
  a.name,
  a.type::text as type,
  a.sector,
  a.exchange,
  a.current_price,
  a.currency,
  a.change_percent,
  a.market_cap,
  a.volume
FROM assets a
WHERE ${whereClause}
ORDER BY ${orderBy}
LIMIT 50
`;

return {
  query,
  criteria,
  originalQuery
};

// Calculate historical performance using price_history data
const assets = $input.all();
const queryData = $('Build Dynamic Query').item.json;

if (assets.length === 0) {
  return {
    noResults: true,
    originalQuery: queryData.originalQuery
  };
}

// Get all asset IDs for historical query
const assetIds = assets.map(a => `'${a.json.id}'`).join(',');

// Single query that gets assets WITH historical performance
const combinedQuery = `
WITH asset_list AS (
  SELECT id, symbol, name, type::text, sector, exchange, 
         current_price, currency, change_percent, market_cap, volume
  FROM assets
  WHERE id IN (${assetIds})
),
latest_prices AS (
  SELECT 
    asset_id,
    date,
    close,
    ROW_NUMBER() OVER (PARTITION BY asset_id ORDER BY date DESC) as rn
  FROM price_history
  WHERE asset_id IN (${assetIds})
    AND date >= CURRENT_DATE - INTERVAL '90 days'
),
price_7d AS (
  SELECT asset_id, close as close_7d FROM latest_prices WHERE rn = 7
),
price_30d AS (
  SELECT asset_id, close as close_30d FROM latest_prices WHERE rn = 30
),
price_90d AS (
  SELECT asset_id, close as close_90d FROM latest_prices WHERE rn = 90
),
current_price_hist AS (
  SELECT asset_id, close as close_current FROM latest_prices WHERE rn = 1
),
volatility AS (
  SELECT 
    asset_id,
    STDDEV(close) / NULLIF(AVG(close), 0) * 100 as volatility_pct
  FROM latest_prices
  WHERE rn <= 30
  GROUP BY asset_id
)
SELECT 
  a.*,
  CASE WHEN p7.close_7d > 0 
    THEN ((a.current_price - p7.close_7d) / p7.close_7d * 100) 
    ELSE NULL END as perf_7d,
  CASE WHEN p30.close_30d > 0 
    THEN ((a.current_price - p30.close_30d) / p30.close_30d * 100) 
    ELSE NULL END as perf_30d,
  CASE WHEN p90.close_90d > 0 
    THEN ((a.current_price - p90.close_90d) / p90.close_90d * 100) 
    ELSE NULL END as perf_90d,
  v.volatility_pct
FROM asset_list a
LEFT JOIN price_7d p7 ON a.id = p7.asset_id
LEFT JOIN price_30d p30 ON a.id = p30.asset_id
LEFT JOIN price_90d p90 ON a.id = p90.asset_id
LEFT JOIN volatility v ON a.id = v.asset_id
`;

return { 
  combinedQuery,
  originalQuery: queryData.originalQuery,
  criteria: queryData.criteria
};

// Format assets with historical performance data
const assetsWithHistory = $input.all();
const queryBuilderData = $input.first().json;

if (assetsWithHistory.length === 0) {
  return {
    noResults: true,
    originalQuery: queryBuilderData.originalQuery
  };
}

// Format assets with FULL market data + historical performance
const assetsFormatted = assetsWithHistory.map(item => {
  const data = item.json;
  const marketCap = data.market_cap;
  const volume = data.volume;
  const changePercent = data.change_percent;

  // Calculate market cap category
  let capCategory = 'Unknown';
  if (marketCap) {
    if (marketCap > 200000000000) capCategory = 'Mega Cap (>200B)';
    else if (marketCap > 10000000000) capCategory = 'Large Cap (10B-200B)';
    else if (marketCap > 2000000000) capCategory = 'Mid Cap (2B-10B)';
    else if (marketCap > 300000000) capCategory = 'Small Cap (300M-2B)';
    else capCategory = 'Micro Cap (<300M)';
  }

  // Determine trend based on multi-timeframe performance
  let trend = 'Neutral';
  const perf7d = data.perf_7d || 0;
  const perf30d = data.perf_30d || 0;
  const perf90d = data.perf_90d || 0;

  if (perf7d > 0 && perf30d > 0 && perf90d > 0) {
    trend = 'Strong Uptrend';
  } else if (perf7d > 0 && perf30d > 0) {
    trend = 'Uptrend';
  } else if (perf7d < 0 && perf30d < 0 && perf90d < 0) {
    trend = 'Strong Downtrend';
  } else if (perf7d < 0 && perf30d < 0) {
    trend = 'Downtrend';
  } else if (perf7d > 0 && perf30d < 0) {
    trend = 'Recovering';
  } else if (perf7d < 0 && perf30d > 0) {
    trend = 'Pullback';
  }

  return {
    symbol: data.symbol,
    name: data.name,
    type: data.type,
    sector: data.sector || 'N/A',
    exchange: data.exchange || 'N/A',
    price: data.current_price,
    currency: data.currency,
    // CURRENT MARKET METRICS
    changePercent: changePercent || 0,
    marketCap: marketCap || 0,
    marketCapFormatted: marketCap ? `$${(marketCap / 1000000000).toFixed(2)}B` : 'N/A',
    capCategory: capCategory,
    volume: volume || 0,
    volumeFormatted: volume ? volume.toLocaleString() : 'N/A',
    // HISTORICAL PERFORMANCE
    perf7d: perf7d ? perf7d.toFixed(2) + '%' : 'N/A',
    perf30d: perf30d ? perf30d.toFixed(2) + '%' : 'N/A',
    perf90d: perf90d ? perf90d.toFixed(2) + '%' : 'N/A',
    volatility: data.volatility_pct ? data.volatility_pct.toFixed(2) + '%' : 'N/A',
    trend: trend
  };
});

return {
  assets: JSON.stringify(assetsFormatted.slice(0, 30), null, 2),
  assetCount: assetsFormatted.length,
  originalQuery: queryBuilderData.originalQuery,
  criteria: queryBuilderData.criteria
};

