// Parse AI response and merge with full asset data                                                                                                   
const allAssets = $('Execute Query').all();                                                                                                           
const prepData = $('Prepare for AI').item.json;                                                                                                       
const aiResponse = $input.item.json;                                                                                                                  

// Check if no results
if (prepData.noResults) {
  return {
    suggestions: [],
    message: `Nessun asset trovato per la tua ricerca: "${prepData.originalQuery}". Prova con criteri diversi.`
  };
}

// Parse AI suggestions - FIX: handle LangChain format
let aiSuggestions = [];
try {
  // Try different response formats
  let content;

  if (Array.isArray(aiResponse) && aiResponse[0]?.message?.content) {
    // LangChain format: array with choices
    content = aiResponse[0].message.content;
  } else if (aiResponse.choices?.[0]?.message?.content) {
    // OpenAI API format
    content = aiResponse.choices[0].message.content;
  } else if (aiResponse.message?.content) {
    // Direct message format
    content = aiResponse.message.content;
  } else if (aiResponse.output) {
    // Output format
    content = aiResponse.output;
  } else if (aiResponse.text) {
    // Text format
    content = aiResponse.text;
  } else {
    // Fallback
    content = JSON.stringify(aiResponse);
  }

  // Clean markdown code blocks
  const cleaned = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  aiSuggestions = JSON.parse(cleaned);

  if (!Array.isArray(aiSuggestions)) {
    aiSuggestions = [aiSuggestions];
  }

  console.log('Parsed AI suggestions:', aiSuggestions.length, 'items');
} catch (e) {
  console.error('Failed to parse AI suggestions:', e);
  // Fallback: return first 10 assets
  aiSuggestions = allAssets.slice(0, 10).map((asset, i) => ({
    symbol: asset.json.symbol,
    reason: `Asset selezionato in base ai criteri di ricerca`,
    score: 80 - (i * 5),
    riskLevel: 'medium',
    expectedReturn: 8,
    timeHorizon: 'medium'
  }));
}

// Merge with full asset data
const suggestions = aiSuggestions.map(suggestion => {
  const asset = allAssets.find(a => a.json.symbol === suggestion.symbol);

  if (!asset) {
    console.log('Asset not found:', suggestion.symbol);
    return null;
  }

  return {
    id: asset.json.id,
    symbol: asset.json.symbol,
    name: asset.json.name,
    currentPrice: parseFloat(asset.json.current_price || 0),
    currency: asset.json.currency || 'EUR',
    type: asset.json.type || 'stock',
    sector: asset.json.sector || 'N/A',
    exchange: asset.json.exchange || 'N/A',
    reason: suggestion.reason || 'Match trovato in base alla tua ricerca',
    score: suggestion.score || 50,
    expectedReturn: suggestion.expectedReturn,
    riskLevel: suggestion.riskLevel || 'medium',
    timeHorizon: suggestion.timeHorizon || 'medium'
  };
}).filter(Boolean);

console.log('Final suggestions:', suggestions.length, 'items');

return { suggestions };
